local a=require('src.core.utils')local b=Grid.sizeX or 2;local c=nil;local d=nil;local e={veryClose={radius=3.5,color=a.hexToRgb('#00a8f9')},close={radius=6.5,color=a.hexToRgb('#34e800')},far={radius=12.5,color=a.hexToRgb('#ea6a00')}}local f={'veryClose','close','far'}function onLoad()c=nil;drawCircles('veryClose')d='veryClose'end;function onUpdate()self.interactable=false;if c==nil or c.held_by_color==nil then return end;local g=self.getPosition()local h=c.getPosition()local i=g-h;local j=math.abs(i.x)local k=math.abs(i.z)if k>j then j=k end;j=j*b;j=math.floor(j)local l=nil;if j<=7 then l='veryClose'elseif j>7 and j<=14 then l='close'elseif j>14 then l='far'end;if l~=d then d=l;drawCircles(d)end end;function drawCircles(m)local n={}local o=self.getScale()local p=1;for q,r in ipairs(f)do if r==m then p=q;break end end;for q=1,p do local r=f[q]local s=e[r]local t=s.radius*b/o.x;local u=generateCirclePoints({x=0,y=0,z=0},t)table.insert(n,{points=u,color=s.color,rotation={0,0,0},fill=true})end;self.setVectorLines(n)end;function generateCirclePoints(v,w)local x=360;local y=360/x;local u={}for q=0,x do local z=math.rad(q*y)local A=v.x+w*math.cos(z)local B=v.z+w*math.sin(z)table.insert(u,{A,v.y,B})end;return u end